'use client'

import { Loader2 } from 'lucide-react'
import dynamic from 'next/dynamic'
import { ChangeEvent, Suspense, useEffect, useRef, useState } from 'react'

// Lazy load ThemeCustomizerButton
const ThemeCustomizerButton = dynamic(() => import('./theme-customizer'), {
  ssr: false,
  loading: () => (
    <button
      aria-label="Loading theme cutomization"
      className="bg-background hover:bg-muted relative ml-2 inline-flex h-8 w-8 items-center justify-center rounded-md border p-1 lg:hidden"
    >
      <Loader2 className="h-4 w-4 animate-spin" />
    </button>
  ),
})

export default function ThemeSwitcher() {
  const ref = useRef<HTMLInputElement>(null)
  const [mounted, setMounted] = useState(false)
  const [isDark, setIsDark] = useState(false)

  useEffect(() => {
    const theme =
      localStorage.getItem('theme') ||
      (window.matchMedia('(prefers-color-scheme: dark)').matches
        ? 'dark'
        : 'light')

    const isDarkMode = theme === 'dark'
    document.documentElement.classList.toggle('dark', isDarkMode)
    setIsDark(isDarkMode)
    setMounted(true)
  }, [])

  const handleTheme = (e: ChangeEvent<HTMLInputElement>) => {
    const isDarkMode = e.currentTarget.checked
    setIsDark(isDarkMode)
    document.documentElement.classList.toggle('dark', isDarkMode)
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light')

    // Dispatch theme change event
    window.dispatchEvent(
      new CustomEvent('themeChange', {
        detail: { theme: isDarkMode ? 'dark' : 'light' },
      }),
    )
  }

  if (!mounted) return null

  return (
    <div className="flex items-center justify-center space-x-2">
      <Suspense fallback={null}>
        <ThemeCustomizerButton />
      </Suspense>
      <input
        name="theme-switcher"
        aria-label="Theme switcher"
        ref={ref}
        type="checkbox"
        checked={isDark}
        onChange={handleTheme}
        className='dark:bg-muted/90 dark:hover:bg-muted dark:focus:bg-muted relative flex h-7 w-[3.2rem] shrink-0 cursor-pointer appearance-none items-center rounded-full !border-none bg-amber-100/90 shadow-xs !ring-0 !ring-offset-0 transition-all duration-200 ease-in-out !outline-none before:ml-0.5 before:inline-block before:h-6 before:w-6 before:translate-x-0 before:transform before:rounded-full before:bg-amber-50 before:bg-[url("data:image/svg+xml;base64,")] before:bg-center before:bg-no-repeat before:shadow before:ring-0 before:transition before:duration-200 before:ease-in-out checked:bg-zinc-600 checked:bg-none checked:before:translate-x-full checked:before:bg-zinc-900 checked:before:bg-[url("data:image/svg+xml;base64,")] focus:border-none focus:ring-0 focus:outline-none'
      />
    </div>
  )
}
